     AND ORDER_VALUE = #{orderValue,jdbcType=VARCHAR}


     AND TEMPLATE_CODE = #{templateCode,jdbcType=VARCHAR}


     AND TEMPLATE_PATH = #{templatePath,jdbcType=VARCHAR}


